#include<iostream>
#include <string>
#include<sstream>

using namespace std;

int multArr(int arr[],int tam) {        //funcion que calcula el producto de un arreglo
	int mult=1;
	for (int i = 0; i < tam; i++)
		mult *= arr[i];
	return mult;
}

int main() {

	string str = "" ;
	string new_str[13];
	int arr[13];
	int mayor = 1;
	int mult;
	int a = 0;

	for (int i = 0; i < (1000 - 12); i++) {
		for ( int j = i; j < 13 + i; j++) {               //extraemos 13 caracteres adyacentes a un arreglo
			new_str[a] = str[j];
			a++;
		}

		for (int k = 0; k < 13; k++) {                
			stringstream geek(new_str[k]);            //convertimos el arreglo de string a int
			geek >> arr[k];
		}

		mult = multArr(arr, 13);                      //calculamos el producto del arreglo

		if (mult > mayor) {                           //comparamos para obtener el mayor producto
			mayor = mult;
		}
		a = 0;
	}

	cout <<"el producto mayor es: "<< mayor << endl;

	return 0;
}